import { PayPalButtons } from "@paypal/react-paypal-js";
import React, { useState } from "react";

const PayPalCheckoutButton = (props) => {
  const { product } = props;
  const [paidFor, setPaidFor] = useState(false);
  const [error, setError] = useState(null);
  const handleApprove = (orderId) => {
    // call back function to fullfill the order

    // if the responce if success
    setPaidFor(true);
    // refresh user's accout or subscription status

    // if the response is error
    // alert the user
  };

  if (paidFor) {
    alert("thank you for your purches");
  }

  if (error) {
    alert("something went wrong");
  }

  return (
    <PayPalButtons
      // onClick= {(data, action) => {
      //   // validate on button click  that the payment is not duplicate
      // }

      // }

      createOrder={(data, actions) => {
        return actions.order.create({
          purchase_units: [
            {
              description: product.description,
              amount: {
                value: product.price,
              },
            },
          ],
        });
      }}
      onApprove={async (data, actions) => {
        const order = await actions.order.capture();
        console.log("order : ", order);
        handleApprove(data.orderId);
      }}
      onCancel={() => {
        // displace the cancel message or redirect use to cancel page or  cart
      }}
      onError={(err) => {
        setError(err);
        console.log(err);
      }}
    />
  );
};

export default PayPalCheckoutButton;
