import React from "react";
import { Link, useParams } from "react-router-dom";
import {
  Row,
  Col,
  Image,
  ListGroup,
  Card,
  Button,
  ListGroupItem,
} from "react-bootstrap";
import Rating from "../components/Rating";
import Products from "../products";
import ProductAvailable from "../components/ProductAvailable";
import CartButton from "../components/CartButton";

const ProductScreen = () => {
  const { id } = useParams();
  const product = Products.find((product) => product._id === id);

  return (
    <>
      <Link className="btn btn-dark my-3" to="/">
        {" "}
        Go Back{" "}
      </Link>

      <Row>
        <Col md={6}>
          <Image src={product.image} alt={product.name} fluid />
        </Col>

        <Col md={3}>
          <ListGroup variant="flush">
            <ListGroupItem style={{ borderBottomWidth: 0 }}>
              <h3>{product.name}</h3>
            </ListGroupItem>

            <ListGroupItem style={{ borderBottomWidth: 0 }}>
              <Rating
                value={product.rating}
                text={`${product.numReviews} reviews`}
              />
            </ListGroupItem>

            <ListGroupItem style={{ borderBottomWidth: 0 }}>
              Price: ${product.price}
            </ListGroupItem>
            <ListGroupItem style={{ borderBottomWidth: 0 }}>
              Description: {product.description}
            </ListGroupItem>
          </ListGroup>
        </Col>

        <Col md={3}>
          <Card>
            <ListGroup variant="flush">
              <ProductAvailable heading={"Price"} value={product.price} />

              <ProductAvailable
                heading={"Status"}
                value={product.countInStock > 0 ? "In Stock" : "Out Of Stock"}
              />

              <CartButton countInStock={product.countInStock} />
            </ListGroup>
          </Card>
        </Col>
      </Row>
    </>
  );
};

export default ProductScreen;
