import React, { useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import ProductDetails from "../components/ProductDetails";
import {
  Row,
  Col,
  Image,
  ListGroup,
  Card,
  ListGroupItem,
} from "react-bootstrap";
import ProductAvailable from "../components/ProductAvailable";
import CartButton from "../components/CartButton";

import { useDispatch, useSelector } from "react-redux";
import { listProductDetails } from "../actions/productActions/productDetailAction";

import Message from "../components/Message";
import Loader from "../components/Loader";

const ProductScreen = () => {
  const { id } = useParams();
  const dispatch = useDispatch();

  const productDetails = useSelector((state) => state.productDetails);
  const { loading, error, product } = productDetails;

  useEffect(() => {
    dispatch(listProductDetails(id));
  }, [dispatch, id]);

  return (
    <>
      <Link className="btn btn-dark my-3" to="/">
        {" "}
        Go Back{" "}
      </Link>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant="danger"> {error} </Message>
      ) : (
        <Row>
          <Col md={6}>
            <Image src={product.image} alt={product.name} fluid />
          </Col>

          <ProductDetails product={product} />

          <Col md={3}>
            <Card>
              <ListGroup variant="flush">
                <ProductAvailable heading={"Price"} value={product.price} />

                <ProductAvailable
                  heading={"Status"}
                  value={product.countInStock > 0 ? "In Stock" : "Out Of Stock"}
                />

                <CartButton countInStock={product.countInStock} />
              </ListGroup>
            </Card>
          </Col>
        </Row>
      )}
    </>
  );
};

export default ProductScreen;
