import React from "react";

function star(value, req) {
  <span>
    <i
      class={
        value >= 1 + req
          ? "fa-solid fa-star"
          : value >= 0.5 + req
          ? "fa-regular fa-star-half-stroke"
          : "fa-regular fa-star"
      }
    ></i>
  </span>;
}

const Rating = ({ value, text }) => {
  const arr = [0, 1, 2, 3, 4];
  return (
    <div className="rating">
      {arr.map((j) => {
        star(value, j);
      })}

      {/* {star(value, 1)}
      {star(value, 2)}
      {star(value, 3)}
      {star(value, 4)} */}

      <span> {text && text} </span>
    </div>
  );
};

export default Rating;
